import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Shield, 
  UserPlus, 
  Edit, 
  Trash2, 
  Eye, 
  EyeOff, 
  Save, 
  X,
  Users
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../context/AuthContext';

const Admin = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();
  const { user: currentUser } = useAuth();

  // Debug logging
  console.log('Admin component state:', { showCreateForm, editingUser, isSubmitting });

  // Check if current user is admin
  useEffect(() => {
    if (currentUser && !currentUser.isAdmin) {
      toast.error('Access denied. Admin privileges required.');
      navigate('/dashboard');
    }
  }, [currentUser, navigate]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/admin/users');
      if (response.data.success) {
        setUsers(response.data.users);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
      toast.error('Failed to load users');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCreateUser = async (userData) => {
    setIsSubmitting(true);
    try {
      const response = await axios.post('/api/admin/users', userData);
      if (response.data.success) {
        toast.success('User created successfully');
        setShowCreateForm(false);
        fetchUsers();
      }
    } catch (error) {
      console.error('Error creating user:', error);
      toast.error(error.response?.data?.message || 'Failed to create user');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleUpdateUser = async (userId, userData) => {
    setIsSubmitting(true);
    try {
      const response = await axios.put(`/api/admin/users/${userId}`, userData);
      if (response.data.success) {
        toast.success('User updated successfully');
        setEditingUser(null);
        fetchUsers();
      }
    } catch (error) {
      console.error('Error updating user:', error);
      toast.error(error.response?.data?.message || 'Failed to update user');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (userId === currentUser?.id) {
      toast.error('You cannot delete your own account');
      return;
    }

    if (window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      try {
        const response = await axios.delete(`/api/admin/users/${userId}`);
        if (response.data.success) {
          toast.success('User deleted successfully');
          fetchUsers();
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        toast.error(error.response?.data?.message || 'Failed to delete user');
      }
    }
  };

  const toggleUserStatus = async (userId, isActive) => {
    try {
      const response = await axios.patch(`/api/admin/users/${userId}/status`, {
        isActive: !isActive
      });
      if (response.data.success) {
        toast.success(`User ${isActive ? 'deactivated' : 'activated'} successfully`);
        fetchUsers();
      }
    } catch (error) {
      console.error('Error updating user status:', error);
      toast.error('Failed to update user status');
    }
  };

  if (isLoading) {
    return (
      <div className="loading">
        Loading admin panel...
      </div>
    );
  }

  return (
    <div>
      <h1 className="page-title">Admin Panel</h1>
      <p className="page-subtitle">
        Manage users, roles, and system access
      </p>

      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <div>
            <h2 style={{ marginBottom: '0.5rem', color: '#333' }}>
              <Shield size={24} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
              User Management
            </h2>
            <p style={{ color: '#666', margin: 0 }}>
              Create, edit, and manage user accounts and permissions
            </p>
          </div>
          
          <button
            onClick={() => {
              console.log('Add User button clicked');
              setShowCreateForm(true);
            }}
            className="btn btn-primary"
            style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
          >
            <UserPlus size={16} />
            Add User
          </button>
        </div>

        {/* Debug info */}
        <div style={{ 
          background: '#f0f0f0', 
          padding: '10px', 
          marginBottom: '1rem', 
          borderRadius: '4px',
          fontSize: '12px'
        }}>
          Debug: showCreateForm = {showCreateForm.toString()}, editingUser = {editingUser ? 'set' : 'null'}
        </div>

        {showCreateForm && (
          <div style={{ 
            marginBottom: '2rem', 
            border: '2px solid #99cc00', 
            padding: '1rem', 
            borderRadius: '8px',
            background: '#f8f9fa'
          }}>
            <h3 style={{ marginBottom: '1rem', color: '#333' }}>
              <UserPlus size={20} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
              Create New User
            </h3>
            
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.target);
              const userData = {
                firstName: formData.get('firstName'),
                lastName: formData.get('lastName'),
                email: formData.get('email'),
                password: formData.get('password'),
                isAdmin: formData.get('isAdmin') === 'admin',
                isActive: formData.get('isActive') === 'active'
              };
              console.log('Creating user:', userData);
              handleCreateUser(userData);
            }}>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
                <div className="form-group">
                  <label className="form-label">First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    className="form-input"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    className="form-input"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    className="form-input"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Password</label>
                  <input
                    type="password"
                    name="password"
                    className="form-input"
                    required
                    minLength={6}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Role</label>
                  <select name="isAdmin" className="form-input">
                    <option value="user">User</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">Status</label>
                  <select name="isActive" className="form-input">
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                  </select>
                </div>
              </div>

              <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={isSubmitting}
                  style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                >
                  <Save size={16} />
                  {isSubmitting ? 'Creating...' : 'Create User'}
                </button>
                
                <button
                  type="button"
                  onClick={() => setShowCreateForm(false)}
                  className="btn btn-secondary"
                  style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                >
                  <X size={16} />
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {editingUser && (
          <div style={{ 
            marginBottom: '2rem', 
            border: '2px solid #00611d', 
            padding: '1rem', 
            borderRadius: '8px',
            background: '#f8f9fa'
          }}>
            <h3 style={{ marginBottom: '1rem', color: '#333' }}>
              <Edit size={20} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
              Edit User: {editingUser.firstName} {editingUser.lastName}
            </h3>
            
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.target);
              const userData = {
                firstName: formData.get('firstName'),
                lastName: formData.get('lastName'),
                email: formData.get('email'),
                password: formData.get('password') || undefined,
                isAdmin: formData.get('isAdmin') === 'admin',
                isActive: formData.get('isActive') === 'active'
              };
              console.log('Updating user:', userData);
              handleUpdateUser(editingUser._id, userData);
            }}>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
                <div className="form-group">
                  <label className="form-label">First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    className="form-input"
                    defaultValue={editingUser.firstName}
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    className="form-input"
                    defaultValue={editingUser.lastName}
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    className="form-input"
                    defaultValue={editingUser.email}
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Password (leave blank to keep current)</label>
                  <input
                    type="password"
                    name="password"
                    className="form-input"
                    minLength={6}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Role</label>
                  <select name="isAdmin" className="form-input" defaultValue={editingUser.isAdmin ? 'admin' : 'user'}>
                    <option value="user">User</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">Status</label>
                  <select name="isActive" className="form-input" defaultValue={editingUser.isActive ? 'active' : 'inactive'}>
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                  </select>
                </div>
              </div>

              <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={isSubmitting}
                  style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                >
                  <Save size={16} />
                  {isSubmitting ? 'Updating...' : 'Update User'}
                </button>
                
                <button
                  type="button"
                  onClick={() => setEditingUser(null)}
                  className="btn btn-secondary"
                  style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                >
                  <X size={16} />
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        <div style={{ overflowX: 'auto' }}>
          <table className="users-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Status</th>
                <th>Last Login</th>
                <th>Registration Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  <td>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                      <Users size={16} style={{ color: '#666' }} />
                      {user.firstName} {user.lastName}
                    </div>
                  </td>
                  <td>{user.email}</td>
                  <td>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '12px',
                      fontSize: '12px',
                      fontWeight: '500',
                      background: user.isAdmin ? '#99cc0020' : '#66620',
                      color: user.isAdmin ? '#99cc00' : '#666',
                      border: `1px solid ${user.isAdmin ? '#99cc00' : '#666'}`
                    }}>
                      {user.isAdmin ? 'Admin' : 'User'}
                    </span>
                  </td>
                  <td>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '12px',
                      fontSize: '12px',
                      fontWeight: '500',
                      background: user.isActive ? '#00471520' : '#ef444420',
                      color: user.isActive ? '#004715' : '#ef4444',
                      border: `1px solid ${user.isActive ? '#004715' : '#ef4444'}`
                    }}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td>
                    {user.lastLogin 
                      ? new Date(user.lastLogin).toLocaleDateString()
                      : 'Never'
                    }
                  </td>
                  <td>
                    {new Date(user.registrationDate).toLocaleDateString()}
                  </td>
                  <td>
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <button
                        onClick={() => {
                          console.log('Edit user clicked:', user);
                          setEditingUser(user);
                        }}
                        className="btn btn-secondary"
                        style={{ padding: '4px 8px', fontSize: '12px' }}
                        disabled={user._id === currentUser?.id}
                      >
                        <Edit size={14} />
                      </button>
                      
                      <button
                        onClick={() => toggleUserStatus(user._id, user.isActive)}
                        className="btn btn-secondary"
                        style={{ 
                          padding: '4px 8px', 
                          fontSize: '12px',
                          background: user.isActive ? '#ef4444' : '#004715',
                          color: 'white'
                        }}
                        disabled={user._id === currentUser?.id}
                      >
                        {user.isActive ? <EyeOff size={14} /> : <Eye size={14} />}
                      </button>
                      
                      <button
                        onClick={() => handleDeleteUser(user._id)}
                        className="btn btn-secondary"
                        style={{ 
                          padding: '4px 8px', 
                          fontSize: '12px',
                          background: '#dc3545',
                          color: 'white'
                        }}
                        disabled={user._id === currentUser?.id}
                      >
                        <Trash2 size={14} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Admin;
